<?php
 namespace Admin\Controller; class ChannelController extends AdminController { public function index(){ $pid = i('get.pid', 0); $map = array('status' => array('gt', -1), 'pid'=>$pid); $list = M('Channel')->where($map)->order('sort asc,id asc')->select(); $this->assign('list', $list); $this->assign('pid', $pid); $this->meta_title = '导航管理'; $this->display(); } public function add(){ if(IS_POST){ $Channel = D('Channel'); $data = $Channel->create(); if($data){ $id = $Channel->add(); if($id){ $this->success('新增成功', U('index')); action_log('update_channel', 'channel', $id, UID); } else { $this->error('新增失败'); } } else { $this->error($Channel->getError()); } } else { $pid = i('get.pid', 0); if(!empty($pid)){ $parent = M('Channel')->where(array('id'=>$pid))->field('title')->find(); $this->assign('parent', $parent); } $this->assign('pid', $pid); $this->assign('info',null); $this->meta_title = '新增导航'; $this->display('edit'); } } public function edit($id = 0){ if(IS_POST){ $Channel = D('Channel'); $data = $Channel->create(); if($data){ if($Channel->save()){ action_log('update_channel', 'channel', $data['id'], UID); $this->success('编辑成功', U('index')); } else { $this->error('编辑失败'); } } else { $this->error($Channel->getError()); } } else { $info = array(); $info = M('Channel')->find($id); if(false === $info){ $this->error('获取配置信息错误'); } $pid = i('get.pid', 0); if(!empty($pid)){ $parent = M('Channel')->where(array('id'=>$pid))->field('title')->find(); $this->assign('parent', $parent); } $this->assign('pid', $pid); $this->assign('info', $info); $this->meta_title = '编辑导航'; $this->display(); } } public function del(){ $id = array_unique((array)I('id',0)); if ( empty($id) ) { $this->error('请选择要操作的数据!'); } $map = array('id' => array('in', $id) ); if(M('Channel')->where($map)->delete()){ action_log('update_channel', 'channel', $id, UID); $this->success('删除成功'); } else { $this->error('删除失败！'); } } public function sort(){ if(IS_GET){ $ids = I('get.ids'); $pid = I('get.pid'); $map = array('status'=>array('gt',-1)); if(!empty($ids)){ $map['id'] = array('in',$ids); }else{ if($pid !== ''){ $map['pid'] = $pid; } } $list = M('Channel')->where($map)->field('id,title')->order('sort asc,id asc')->select(); $this->assign('list', $list); $this->meta_title = '导航排序'; $this->display(); }elseif (IS_POST){ $ids = I('post.ids'); $ids = explode(',', $ids); foreach ($ids as $key=>$value){ $res = M('Channel')->where(array('id'=>$value))->setField('sort', $key+1); } if($res !== false){ $this->success('排序成功！'); }else{ $this->eorror('排序失败！'); } }else{ $this->error('非法请求！'); } } } 