<?php
 namespace Common\Api; class ConfigApi { public static function lists(){ $map = array('status' => 1); $data = M('Config')->where($map)->field('type,name,value')->select(); $config = array(); if($data && is_array($data)){ foreach ($data as $value) { $config[$value['name']] = self::parse($value['type'], $value['value']); } } return $config; } private static function parse($type, $value){ switch ($type) { case 3: $array = preg_split('/[,;\r\n]+/', trim($value, ",;\r\n")); if(strpos($value,':')){ $value = array(); foreach ($array as $val) { list($k, $v) = explode(':', $val); $value[$k] = $v; } }else{ $value = $array; } break; } return $value; } }