<?php
 namespace Common\Api; class ModelApi { public static function get_document_model($id = null, $field = null){ static $list; if(!(is_numeric($id) || is_null($id))){ return ''; } if(empty($list)){ $list = S('DOCUMENT_MODEL_LIST'); } if(empty($list)){ $map = array('status' => 1, 'extend' => 1); $model = M('Model')->where($map)->field(true)->select(); foreach ($model as $value) { $list[$value['id']] = $value; } S('DOCUMENT_MODEL_LIST', $list); } if(is_null($id)){ return $list; } elseif(is_null($field)){ return $list[$id]; } else { return $list[$id][$field]; } } public static function get_model_by_id($id){ return $model = M('Model')->getFieldById($id,'title'); } public static function get_model_attribute($model_id, $group = true){ static $list; if(empty($model_id) || !is_numeric($model_id)){ return ''; } if(empty($list)){ $list = S('attribute_list'); } if(!isset($list[$model_id])){ $map = array('model_id'=>$model_id); $extend = M('Model')->getFieldById($model_id,'extend'); if($extend){ $map = array('model_id'=> array("in", array($model_id, $extend))); } $info = M('Attribute')->where($map)->select(); $list[$model_id] = $info; } $attr = array(); foreach ($list[$model_id] as $value) { $attr[$value['id']] = $value; } if($group){ $sort = M('Model')->getFieldById($model_id,'field_sort'); if(empty($sort)){ $group = array(1=>array_merge($attr)); }else{ $group = json_decode($sort, true); $keys = array_keys($group); foreach ($group as &$value) { foreach ($value as $key => $val) { $value[$key] = $attr[$val]; unset($attr[$val]); } } if(!empty($attr)){ $group[$keys[0]] = array_merge($group[$keys[0]], $attr); } } $attr = $group; } return $attr; } public static function parse_field_attr($string) { if(0 === strpos($string,':')){ return eval(substr($string,1).';'); } $array = preg_split('/[,;\r\n]+/', trim($string, ",;\r\n")); if(strpos($string,':')){ $value = array(); foreach ($array as $val) { list($k, $v) = explode(':', $val); $value[$k] = $v; } }else{ $value = $array; } return $value; } }