<?php
 namespace User\Api; use User\Api\Api; use User\Model\UcenterMemberModel; class UserApi extends Api{ protected function _init(){ $this->model = new UcenterMemberModel(); } public function register($username, $password, $email, $mobile = ''){ return $this->model->register($username, $password, $email, $mobile); } public function login($username, $password, $type = 1){ return $this->model->login($username, $password, $type); } public function info($uid, $is_username = false){ return $this->model->info($uid, $is_username); } public function checkUsername($username){ return $this->model->checkField($username, 1); } public function checkEmail($email){ return $this->model->checkField($email, 2); } public function checkMobile($mobile){ return $this->model->checkField($mobile, 3); } public function updateInfo($uid, $password, $data){ if($this->model->updateUserFields($uid, $password, $data) !== false){ $return['status'] = true; }else{ $return['status'] = false; $return['info'] = $this->model->getError(); } return $return; } } 